import React, { Component } from "react";
import { Redirect } from "react-router-dom"
export default class Login extends Component {
    
    constructor() {
        super();
        const token = localStorage.getItem("token")
        let loggedIn = true
        if(token== null){
            loggedIn = false
        }
        this.state = {
            name: "",
            password: "",
            nameError: "",
            passwordError: "",
            loggedIn:""
        }
    }

    valid() {
        if (!this.state.name.includes("@") && this.state.password.length < 5) {
            this.setState(
                { nameError: "invalid name", passwordError: "password lenght should be more than 5" }
            )
        }
        else if (!this.state.name.includes("@")) {
            this.setState({ nameError: "Invalid Name" })
        }
        else if (this.state.password.length < 5) {
            this.setState({ passwordError: "password lenght should be more than 5" })
        }
        else {
            return true;
        }
    }
    submit()
    {
      this.setState({nameError:"", passwordError:""})
        if(this.valid() && this.state.name==="some@gmail.com" && this.state.password === "12345678" )
        {  
            //comment command cntl k + c
            //for authentication purpose we have store token in local storage
            localStorage.setItem("token", "ieihfiwerhfihiwirihiu")
             this.setState(
          {
              loggedIn:true
          }
      )
            alert("form has been submited")
        }
     
    }
    render() {
        if(this.state.loggedIn){
            return <Redirect to={"/Admin"} />
        }
        return (
            <div className="form">
                <h2>Login</h2>
                <label>Email Address</label><br></br>
                <input placeholder="Enter Email" type="text" onChange={(event) => { this.setState({ name: event.target.value }) }} />
                <p style={{ color: "red" }}>{this.state.nameError}</p>
                <label>Password</label><br></br>
                <input placeholder="Enter Password" type="password" onChange={(event) => { this.setState({ password: event.target.value }) }} />
                <p style={{ color: "red" }}>{this.state.passwordError}</p>
                <button onClick={() => this.submit()}>Submit</button>

            </div>

        );
    }

}
