import React, { Component } from "react";
export default class Login extends Component{
    constructor() {
        super();
        this.state={
                      name:"",
                      password:"",
                      nameError: "",
                      passwordError: ""
                    };
                }
                valid()
                {
                    if(!this.state.name.includes("@") && this.state.password.length<5)
                    {
                        this.setState({nameError:"Invalid Name", passwordError:"password lenght should be more than 5"})
                    }
                    else if(!this.state.name.includes("@"))
                    {
                        this.setState({nameError:"Invalid Name"})
                    }
                   else if(this.state.password.length<5)
                    {
                        this.setState({passwordError:"password lenght should be more than 5"})
                    }
                    else{
                        return true;
                    }
                   
    
                }
                   submit()
                  {
                    this.setState({nameError:"", passwordError:""})
                      if(this.valid())
                      {
                          alert("form has been submited")
                      }
                    
                  }
                render(){
                    return(
                        <div>
                          <form className="form">  
                            <h2>login</h2>
                        <input type="text" placeHolder="Enter your email" onChange={(event)=>{this.setState({name:event.target.value})}} />
                        <p style={{color:"red",fontSize:"14px"}}>{this.state.nameError}</p>
                          <input type="password" placeHolder="Enter your password" onChange={(event)=>{this.setState({password:event.target.value})}} />
                          <p style={{color:"red",fontSize:"14px"}}>{this.state.passwordError}</p>
                      <button onClick={()=>this.submit()}>Submit</button>
                      </form>
    
                        </div>    
    
                    );
                }
                   
            }
